sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var gdtNeeded = new GlideDateTime(current.u_when_needed);
    var gdtNow = new GlideDateTime();

    var today = gdtNow.getLocalDate();
    var isToday = gdtNeeded.getLocalDate();

    //     if (gdtNeeded.before(gdtNow)) {
    //         gs.addInfoMessage("The When Needed date can not be in the past.");
    //         current.setAbortAction(true);
    //     }

    var niUtil = new NeedItUtils();
    var isPast = niUtil.isDatePast(current.u_when_needed);

    if (isPast) {
        gs.addInfoMessage("The When Needed date can not be in the past##############.");
        current.setAbortAction(true);
    }

    if (today.compareTo(isToday) == 0) {
        gs.addInfoMessage("When Needed date can not be today.  Pleaes set it to after " + today + ".");
        current.setAbortAction(true);
    }

})(current, previous);
